import React from 'react'
import classes from './AvailableMeals.module.css'
import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';

function AvailableMeals() {
    const DUMMY_MEALS = [
        {
          id: 'm1',
          name: 'Sushi',
          description: 'Finest fish and veggies',
          price: 22.99,
        },
        {
          id: 'm2',
          name: 'Schnitzel',
          description: 'A german specialty!',
          price: 16.5,
        },
        {
          id: 'm3',
          name: 'Barbecue Burger',
          description: 'American, raw, meaty',
          price: 12.99,
        },
        {
          id: 'm4',
          name: 'Green Bowl',
          description: 'Healthy...and green...',
          price: 18.99,
        },
      ];
    //   const mealsList =DUMMY_MEALS.map(meal => <li>{meal.name}</li>)
      const mealsList =DUMMY_MEALS.map(meal => <MealItem id={meal.id}key={meal.id} name={meal.name} description={meal.description} price={meal.price}/>)
      //now this approach was temporar coz we gonna create a separate meal item componenet in a new folder coz there we wil have meal item and a form to add new meal to cart so further 2 componenets
  return (
   <section className={classes.meals}>
    <Card>
    <ul>
        {mealsList}
    </ul>
    </Card>
    
   </section>
  )
}

export default AvailableMeals
